import { useState } from "react";
import './App.css';

function Alert({ headerText, bodyText }) {
  return (
    <div className="alert">
      {headerText && <h5>{headerText}</h5>}
      {bodyText}
    </div>
  );
}

function App() {
  const [headerText, setHeaderText] = useState("Enter header text");
  const [bodyText, setBodyText] = useState("Enter body text");

  return (
    <div className="App">
      <div className="setup">
        <p>Alert Setup</p>
        <div className="setup--header">
          <label>
            Header Text (Optional):
            <input value={headerText}></input>
          </label>
        </div>
        <div className="setup--body">
          <label>
            Body Text:
            <textarea value={bodyText} rows={4} cols={40} />
          </label>
        </div>
      </div>
      <div className="examples">
        <div className="example">
          <p>Current</p>
          <Alert
            header="Current Example"
            body="hey"
          />
        </div>
        <div className="example">
          <p>New</p>
          <Alert
            header="New Example"
            body="heyy"
          />
        </div>
      </div>
    </div>
  );
}

export default App;
