import { useState } from "react";
import classNames from "classnames";
import { ALERT_EMPHASES, ALERT_ICONS, LINK_ARROW, DISMISS } from "./AlertSettings";
import './App.scss';

// TODO
// 1. Update current alert styles
// 2. Update new alert styles
// 3. Move Alert to its own file
// 4. Improve alert CSS

function App() {
  const [headerText, setHeaderText] = useState("Header");
  const [bodyText, setBodyText] = useState("This is the body text.");
  const [actionLinkLabel, setActionLinkLabel] = useState("Action Link");
  const [emphasis, setEmphasis] = useState("info");
  const [reducedOpacity, setReducedOpacity] = useState(false);
  const [stroke, setStroke] = useState(false);
  const [iconColor, setIconColor] = useState(false);

  return (
    <div className="App">
      <div className="section setup">
        <div className="section--header">
          <h1>Setup</h1>
        </div>
        <div className="section--main">
          <div>
            <div className="setup--block">
              <p className="label">Emphasis</p>
              {
                ALERT_EMPHASES.map(option => (
                  <label>
                    <input
                      type="radio"
                      name="emphasis"
                      key={option}
                      id={option}
                      value={option}
                      checked={option === emphasis}
                      onChange={event => {
                        setEmphasis(event.target.value)
                      }}
                    />
                    {option}
                  </label>
                ))
              }
            </div>
            <div className="setup--block">
              <p className="label">New Alert Options</p>
              <label>
                <input
                  type="checkbox"
                  checked={reducedOpacity}
                  onChange={event => {
                    setReducedOpacity(event.target.checked)
                  }}
                />
                Lighter background
              </label>
              <label>
                <input
                  type="checkbox"
                  checked={stroke}
                  onChange={event => {
                    setStroke(event.target.checked)
                  }}
                />
                Stroke
              </label>
              <label>
                <input
                  type="checkbox"
                  checked={iconColor}
                  onChange={event => {
                    setIconColor(event.target.checked)
                  }}
                />
                Colored Icon
              </label>
            </div>
          </div>
          <div>
            <div className="setup--block">
              <label>
                <p className="label">Header Text (Optional)</p>
                <input
                  value={headerText}
                  onChange={e => setHeaderText(e.target.value)} />
              </label>
            </div>
            <div className="setup--block">
              <label>
                <p className="label">Body Text</p>
                <textarea
                  value={bodyText}
                  onChange={e => setBodyText(e.target.value)}
                  rows={5} />
              </label>
            </div>
            <div className="setup--block">
              <label>
                <p className="label">Action Link Label (Optional)</p>
                <input
                  value={actionLinkLabel}
                  onChange={e => setActionLinkLabel(e.target.value)} />
              </label>
            </div>
          </div>
        </div>
      </div>

      <div className="section examples">
        <h1 className="section--header">
          Examples
        </h1>
        <div className="section--main">
          <div className="example">
            <p className="label">Current</p>
            <AlertCurrent
              emphasis={emphasis}
              header={headerText}
              body={bodyText}
              actionLink={actionLinkLabel}
            />
          </div>
          <div className="example">
            <p className="label">New</p>
            <AlertNew
              emphasis={emphasis}
              header={headerText}
              body={bodyText}
              actionLink={actionLinkLabel}
              reducedOpacity={reducedOpacity}
              stroke={stroke}
              iconColor={iconColor}
            />
          </div>
        </div>
      </div>
    </div >
  );
}

function AlertCurrent({ emphasis, header, body, actionLink }) {
  return (
    <div className={`Alert current ${emphasis}`}>
      <div className="Alert--icon">
        {ALERT_ICONS[emphasis]}
      </div>
      <div className="Alert--content">
        {header && <div className="Alert--header">
          <h2>{header}</h2>
        </div>}
        <div className="Alert--body">
          {body}
        </div>
        {actionLink && <div className="Alert--action">
          <button className="action-link">{actionLink}</button>
        </div>}
      </div>
    </div>
  );
}

function AlertNew({ emphasis, header, body, actionLink, reducedOpacity, stroke, iconColor }) {

  return (
    <div className={classNames("Alert new", emphasis, { light: reducedOpacity, stroke: stroke })}>
      <div className={classNames("Alert--icon", iconColor && emphasis)}>
        {ALERT_ICONS[emphasis]}
      </div>
      <div className="Alert--content">
        {header && <div className="Alert--header">
          <h3>{header}</h3>
        </div>}
        <div className="Alert--body">
          {body}
        </div>
        {actionLink && <div className="Alert--action">
          <button className="action-link">{actionLink}{LINK_ARROW}</button>
        </div>}
      </div>
    </div >
  );
}

export default App;
