import { useState } from "react";
import './App.css';



function App() {
  const [headerText, setHeaderText] = useState("Enter header text");
  const [bodyText, setBodyText] = useState("Enter body text");

  function Alert({ header, body }) {
    return (
      <div className="alert">
        {header && <h5>{header}</h5>}
        {body}
      </div>
    );
  }

  return (
    <div className="App">
      <div className="setup">
        <p>Alert Setup</p>
        <div className="setup--header">
          <label>
            Header Text (Optional):
            <input
              value={headerText}
              onChange={e => setHeaderText(e.target.value)} />
          </label>
        </div>
        <div className="setup--body">
          <label>
            Body Text:
            <textarea
              value={bodyText}
              onChange={e => setBodyText(e.target.value)}
              rows={4}
              cols={40} />
          </label>
        </div>
      </div>
      <div className="examples">
        <div className="example">
          <p>Current</p>
          <Alert
            header={headerText}
            body={bodyText}
          />
        </div>
        <div className="example">
          <p>New</p>
          <Alert
            header={headerText}
            body={bodyText}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
