import { useState } from "react";
import './App.css';

function Alert({ header, body, actionLink }) {
  return (
    <div className="alert">
      <div className="icon"></div>
      {header && <div className="alert--header">
        <h3>{header}</h3>
      </div>}
      <div className="alert--body">
        {body}
      </div>
      {actionLink && <div className="alert--action">
        <button>{actionLink}</button>
      </div>}
    </div>
  );
}

function App() {
  const [headerText, setHeaderText] = useState("Enter header text");
  const [bodyText, setBodyText] = useState("Enter body text");
  const [actionLinkLabel, setActionLinkLabel] = useState("Action Link Label");

  return (
    <div className="App">
      <div className="setup">
        <h1>Alert Setup</h1>
        <div className="setup--header">
          <label>
            <p className="label">Header Text (Optional)</p>
            <input
              value={headerText}
              onChange={e => setHeaderText(e.target.value)} />
          </label>
        </div>
        <div className="setup--body">
          <label>
            <p className="label">Body Text</p>
            <textarea
              value={bodyText}
              onChange={e => setBodyText(e.target.value)}
              rows={5}
              cols={80} />
          </label>
          <div className="setup--action">
            <label>
              <p className="label">Action Link Label (Optional)</p>
              <input
                value={actionLinkLabel}
                onChange={e => setActionLinkLabel(e.target.value)} />
            </label>
          </div>
        </div>
      </div>
      <div className="examples">
        <div className="example current">
          <p>Current</p>
          <Alert
            header={headerText}
            body={bodyText}
            actionLink={actionLinkLabel}
          />
        </div>
        <div className="example new">
          <p>New</p>
          <Alert
            header={headerText}
            body={bodyText}
            actionLink={actionLinkLabel}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
