import { useState } from "react";
import './App.css';

function Alert({ header, body, actionLink }) {
  return (
    <div className="alert">
      <div className="alert--icon"></div>
      <div className="alert--content">
        {header && <div className="alert--header">
          <h3>{header}</h3>
        </div>}
        <div className="alert--body">
          {body}
        </div>
        {actionLink && <div className="alert--action">
          <button className="actionLink">{actionLink}</button>
        </div>}
      </div>
    </div>
  );
}

function App() {
  const [headerText, setHeaderText] = useState("Alert header");
  const [bodyText, setBodyText] = useState("This is alert body text.");
  const [actionLinkLabel, setActionLinkLabel] = useState("Action Link");

  return (
    <div className="App">
      <div className="setup">
        <h1>Alert Setup</h1>
        <div className="setup--header">
          <label>
            <p className="label">Header Text (Optional)</p>
            <input
              value={headerText}
              onChange={e => setHeaderText(e.target.value)} />
          </label>
        </div>
        <div className="setup--body">
          <label>
            <p className="label">Body Text</p>
            <textarea
              value={bodyText}
              onChange={e => setBodyText(e.target.value)}
              rows={5}
              cols={80} />
          </label>
          <div className="setup--action">
            <label>
              <p className="label">Action Link Label (Optional)</p>
              <input
                value={actionLinkLabel}
                onChange={e => setActionLinkLabel(e.target.value)} />
            </label>
          </div>
        </div>
      </div>
      <div className="examples">
        <h1 className="examples--header">Examples</h1>
        <div className="examples--main">
          <div className="example current">
            <p>Current</p>
            <Alert
              header={headerText}
              body={bodyText}
              actionLink={actionLinkLabel}
            />
          </div>
          <div className="example new">
            <p>New</p>
            <Alert
              header={headerText}
              body={bodyText}
              actionLink={actionLinkLabel}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
