import Intents from '@ux/intents';
import { UX_SPACE_AS } from './options';

// eslint-disable-next-line max-statements
export default (function () {

  const intents = new Intents();

  intents.create('@ux/space', {
    features: Object.keys(UX_SPACE_AS).reduce((acc, as) => {
      return Object.assign(acc, { [as]: { styleProperties: ['paddingFactor', 'gapFactor'] } });
    }, {})
  });

  Object.entries(UX_SPACE_AS).forEach(([as, { paddingFactor, gapFactor }]) => {
    intents.uxSpace[as].paddingFactor = paddingFactor;
    intents.uxSpace[as].gapFactor = gapFactor;
  });

  return intents;

}());
