/**
 * Each key is a named level of density.
 * Each factor is a number which is multiplied against
 *  the grid unit (intents.ux.box.density) value which determines the space.
 */
export const UX_SPACE_AS = {
  module: {
    paddingFactor: 8,
    gapFactor: 3
  },
  block: {
    paddingFactor: 5,
    gapFactor: 3
  },
  lockup: {
    paddingFactor: 2,
    gapFactor: 1
  }
};

const asOptions = Object.keys(UX_SPACE_AS).reduce((acc, key) => ({ [key.toUpperCase()]: key, ...acc }), []);

export default asOptions;
